mapper.select:

select * from(
	select rownum rnum, AA.* from(
		select * 
		from PROJECT_EXHIBITION
		where
		START_DATE <= #{yy}||'-'||#{MM}||'-'||#{dd}
		AND
		END_DATE >= #{yy}||'-'||#{MM}||'-'||#{dd}
		order by idx desc
	)AA where rownum <= #{end}
) where rnum >= #{start}


select * from(
	select rownum rnum, AA.* from(
		select * 
		from PROJECT_EXHIBITION
		where
		END_DATE < #{yy}||'-'||#{MM}||'-'||#{dd}
		order by idx desc
	)AA where rownum <= #{end}
) where rnum >= #{start}
