mapper.select:

select * from (
	select rownum rnum, TT.* from (
		select * from PROJECT_MEMBER order by idx desc
	) TT where rownum <= #{end}
) where rnum >= #{start}

mapper.update:
update
PROJECT_MEMBER
set
password = #{password}, phone = #{phone}, address = #{address}, email = #{email}
where
id = #{id}
